function showCustomDialog(content){d=document;if(d.getElementById("modalContainer"))return;mObj=d.getElementsByTagName("body")[0].appendChild(d.createElement("div"));mObj.id="modalContainer";mObj.style.height=document.documentElement.scrollHeight+"px";alertObj=mObj.appendChild(d.createElement("div"));alertObj.id="alertBox";if(d.all&&!window.opera)alertObj.style.top=document.body.scrollTop+"px";alertObj.style.left=(d.documentElement.scrollWidth- alertObj.offsetWidth)/2 + "px";
alertObj.innerHTML=content;try{document.getElementById('webdav_url_field').focus();document.getElementById('webdav_url_field').select();}catch(e){}}
function removeCustomDialog(){document.getElementsByTagName("body")[0].removeChild(document.getElementById("modalContainer"));}